CREATE TABLE "public"."ratings" ("id" serial NOT NULL, "recipe_id" integer NOT NULL, "user_id" integer NOT NULL, "rating" integer NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("recipe_id") REFERENCES "public"."recipes"("id") ON UPDATE restrict ON DELETE cascade, FOREIGN KEY ("user_id") REFERENCES "public"."users"("id") ON UPDATE restrict ON DELETE restrict, CONSTRAINT "rating_limit" CHECK (rating >= 1 and rating <= 5));
