CREATE TABLE "public"."purchases" ("id" serial NOT NULL, "user_id" integer NOT NULL, "recipe_id" integer NOT NULL, "payment_reference" text NOT NULL, "payment_status" text NOT NULL, "amount" numeric NOT NULL, "created_at" timestamptz NOT NULL DEFAULT now(), PRIMARY KEY ("id") , FOREIGN KEY ("user_id") REFERENCES "public"."users"("id") ON UPDATE restrict ON DELETE cascade, FOREIGN KEY ("recipe_id") REFERENCES "public"."recipes"("id") ON UPDATE restrict ON DELETE cascade);
